mapper.select:


		SELECT * FROM(  		
			SELECT rownum as rn, reply_id, product_id, member_id, content, rating, updateDate, regDate
			FROM Product_Reply
			WHERE rownum <= #{pageNum} * #{amount} AND product_id = #{product_id}	
			order by regDate desc  	
  			)
  		where rn > (#{pageNum} -1) * #{amount}

SELECT count(*)
FROM Product_Reply
WHERE product_id = #{product_id}
